import React from "react";
import { books } from "../../data/books"; // Correctly importing the data
import Book from "../Book"; // Assuming this is located in the same directory

const BestBooks = ({ id }) => {
  console.log(books); // Just to check if the books data is available


  return (
    <div className="books">
      {books
        .filter((book) => {
          if (id) {
            // Only return books with a rating of 5 and not the current book (if id is provided)
            return book.rating === 5 && book.id !== id;
          }
          // Return all books with a rating of 5
          return book.rating === 5;
        })
        .slice(0, 4) // Limit to 4 books
        .map((book) => (
          <Book book={book} key={book.id} /> // Pass each book to the Book component
        ))}
    </div>
  );
};

export default BestBooks;
